require('jsdom-global')();  // Automatically mock the DOM environment

import * as createGridModule from '../generateGrid.js';
import * as chooseMineModule from '../chooseNumberOfMine.js';
import * as startGameModule from '../startGame.js';
import * as movePlayerModule from '../movePlayer.js';
import '../additionalFunctionality.js';  // Ensure the event listeners are set up

describe('additionalFunctionality.js', () => {
    let gridSizeElement, startButtonElement, mineCountElement;

    beforeEach(() => {
        // Mocking the DOM elements
        document.body.innerHTML = `
            <select id="grid-size"></select>
            <button id="start-game-btn"></button>
            <input id="mine-count" />
            <div id="grid-container"></div>
            <div id="message"></div>
        `;

        gridSizeElement = document.getElementById('grid-size');
        startButtonElement = document.getElementById('start-game-btn');
        mineCountElement = document.getElementById('mine-count');

        // Spy on the imported functions
        spyOn(createGridModule, 'generateGrid').and.callThrough();
        spyOn(chooseMineModule, 'chooseNumberOfMine').and.callThrough();
        spyOn(startGameModule, 'startGame').and.callThrough();
        spyOn(movePlayerModule, 'movePlayer').and.callThrough();
    });

    it('should call generateGrid and chooseNumberOfMine when grid size changes', () => {
        // Simulate grid size change
        const event = new Event('change');
        gridSizeElement.dispatchEvent(event);

        expect(createGridModule.generateGrid).toHaveBeenCalled();
        expect(chooseMineModule.chooseNumberOfMine).toHaveBeenCalled();
    });

    it('should call startGame when start button is clicked', () => {
        // Simulate button click
        const event = new Event('click');
        startButtonElement.dispatchEvent(event);

        expect(startGameModule.startGame).toHaveBeenCalled();
    });

    it('should call movePlayer with correct direction when arrow key is pressed', () => {
        const keyboardEventUp = new KeyboardEvent('keydown', { key: 'ArrowUp' });
        document.dispatchEvent(keyboardEventUp);
        expect(movePlayerModule.movePlayer).toHaveBeenCalledWith('up');

        const keyboardEventDown = new KeyboardEvent('keydown', { key: 'ArrowDown' });
        document.dispatchEvent(keyboardEventDown);
        expect(movePlayerModule.movePlayer).toHaveBeenCalledWith('down');

        const keyboardEventLeft = new KeyboardEvent('keydown', { key: 'ArrowLeft' });
        document.dispatchEvent(keyboardEventLeft);
        expect(movePlayerModule.movePlayer).toHaveBeenCalledWith('left');

        const keyboardEventRight = new KeyboardEvent('keydown', { key: 'ArrowRight' });
        document.dispatchEvent(keyboardEventRight);
        expect(movePlayerModule.movePlayer).toHaveBeenCalledWith('right');
    });
});

